import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = token;
  }
  return config;
});

// Handle response errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  login: async (email, password) => {
    const response = await api.post('/auth/login', { email, password });
    return response.data;
  },
  
  // Registration removed - users can only be created by admins through student management
};

// Document API
export const documentAPI = {
  getAll: async () => {
    const response = await api.get('/document');
    return response.data;
  },
  
  upload: async (formData) => {
    const response = await api.post('/document', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  },
  
  download: (filePath) => {
    return `${API_BASE_URL.replace('/api', '')}/${filePath}`;
  },
};

// Category API
export const categoryAPI = {
  getAll: async () => {
    const response = await api.get('/category');
    return response.data;
  },
  
  getTree: async () => {
    const response = await api.get('/category/tree');
    return response.data;
  },
  
  getByParent: async (parentId = null) => {
    const endpoint = parentId ? `/category/by-parent/${parentId}` : '/category/by-parent';
    const response = await api.get(endpoint);
    return response.data;
  },
  
  create: async (name, parentCategory = null) => {
    const response = await api.post('/category', { name, parentCategory });
    return response.data;
  },
  
  delete: async (id) => {
    const response = await api.delete(`/category/${id}`);
    return response.data;
  },
};

// Student API
export const studentAPI = {
  getAll: async () => {
    const response = await api.get('/students');
    return response.data;
  },
  
  create: async (email, password, terminationDate) => {
    const response = await api.post('/students', { email, password, terminationDate });
    return response.data;
  },
  
  updateTermination: async (id, terminationDate) => {
    const response = await api.put(`/students/${id}/termination`, { terminationDate });
    return response.data;
  },
  
  delete: async (id) => {
    const response = await api.delete(`/students/${id}`);
    return response.data;
  },
};

// User API
export const userAPI = {
  getDashboard: async () => {
    const response = await api.get('/user/dashboard');
    return response.data;
  },
};

export default api;
