import React, { useState, useEffect } from 'react';
import { X, AlertCircle, Bell } from 'lucide-react';
import noticeAPI from '../services/noticeAPI';

const NoticeDisplay = () => {
  const [notices, setNotices] = useState([]);
  const [dismissedNotices, setDismissedNotices] = useState(new Set());

  useEffect(() => {
    loadNotices();
  }, []);

  const loadNotices = async () => {
    try {
      const data = await noticeAPI.getBannerMarqueeNotices();
      setNotices(data);
    } catch (error) {
      console.error('Failed to load notices:', error);
    }
  };

  const handleDismiss = async (noticeId) => {
    try {
      await noticeAPI.markAsRead(noticeId);
      setDismissedNotices(prev => new Set([...prev, noticeId]));
    } catch (error) {
      console.error('Failed to mark notice as read:', error);
    }
  };

  const visibleNotices = notices.filter(notice => !dismissedNotices.has(notice._id));
  const bannerNotices = visibleNotices.filter(notice => notice.type === 'banner');
  const marqueeNotices = visibleNotices.filter(notice => notice.type === 'marquee');

  if (visibleNotices.length === 0) return null;

  return (
    <div className="relative">
      {/* Banner Notices */}
      {bannerNotices.map((notice) => (
        <div
          key={notice._id}
          className="relative bg-blue-600 text-white"
        >
          <div className="max-w-7xl mx-auto py-3 px-3 sm:px-6 lg:px-8">
            <div className="flex items-center justify-between flex-wrap">
              <div className="w-0 flex-1 flex items-center">
                <span className="flex p-2 rounded-lg bg-blue-800">
                  <AlertCircle className="h-6 w-6 text-white" />
                </span>
                <div className="ml-3 font-medium">
                  <span className="block sm:inline font-semibold">{notice.title}</span>
                  <span className="block sm:inline sm:ml-2">{notice.content}</span>
                </div>
              </div>
              <div className="order-2 flex-shrink-0 sm:order-3 sm:ml-3">
                <button
                  type="button"
                  onClick={() => handleDismiss(notice._id)}
                  className="-mr-1 flex p-2 rounded-md hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-white sm:-mr-2"
                >
                  <X className="h-6 w-6 text-white" />
                </button>
              </div>
            </div>
          </div>
        </div>
      ))}

      {/* Marquee Notices */}
      {marqueeNotices.length > 0 && (
        <div className="bg-yellow-50 border-b border-yellow-200">
          <div className="max-w-7xl mx-auto py-2 px-3 sm:px-6 lg:px-8">
            <div className="flex items-center justify-between">
              <div className="flex items-center flex-1 min-w-0">
                <Bell className="h-5 w-5 text-yellow-600 flex-shrink-0" />
                <div className="ml-3 overflow-hidden">
                  <div className="marquee-container">
                    <div className="marquee-content">
                      {marqueeNotices.map((notice, index) => (
                        <span key={notice._id} className="text-yellow-800">
                          <span className="font-semibold">{notice.title}:</span> {notice.content}
                          {index < marqueeNotices.length - 1 && <span className="mx-8">â€¢</span>}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
              <button
                type="button"
                onClick={() => {
                  marqueeNotices.forEach(notice => handleDismiss(notice._id));
                }}
                className="ml-3 flex-shrink-0 p-1 rounded-md hover:bg-yellow-100 focus:outline-none focus:ring-2 focus:ring-yellow-500"
              >
                <X className="h-4 w-4 text-yellow-600" />
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Marquee CSS */}
      <style jsx>{`
        .marquee-container {
          overflow: hidden;
          white-space: nowrap;
        }
        
        .marquee-content {
          display: inline-block;
          animation: marquee 30s linear infinite;
        }
        
        @keyframes marquee {
          0% { transform: translateX(100%); }
          100% { transform: translateX(-100%); }
        }
        
        .marquee-container:hover .marquee-content {
          animation-play-state: paused;
        }
      `}</style>
    </div>
  );
};

export default NoticeDisplay;
