import React, { useState, useEffect } from 'react';
import { documentAPI, categoryAPI } from '../services/api';
import { FileText, Search, ChevronDown, ChevronRight, Folder, FolderOpen, Eye, X } from 'lucide-react';

const Documents = () => {
  const [documents, setDocuments] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [expandedCategories, setExpandedCategories] = useState(new Set());
  const [categoryTree, setCategoryTree] = useState([]);
  const [categorizedDocuments, setCategorizedDocuments] = useState({});
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [viewerLoading, setViewerLoading] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    if (categories.length > 0 && documents.length > 0) {
      organizeCategoriesAndDocuments();
    }
  }, [categories, documents, searchTerm]);

  const loadData = async () => {
    try {
      const [docsResponse, categoriesResponse] = await Promise.all([
        documentAPI.getAll(),
        categoryAPI.getTree(),
      ]);
      
      setDocuments(docsResponse);
      setCategories(categoriesResponse);
    } catch (error) {
      console.error('Error loading documents:', error);
    } finally {
      setLoading(false);
    }
  };

  const organizeCategoriesAndDocuments = () => {
    // Filter documents based on search term
    const filteredDocs = documents.filter(doc =>
      doc.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Group documents by category
    const docsByCategory = {};
    const uncategorizedDocs = [];

    filteredDocs.forEach(doc => {
      if (doc.category && doc.category._id) {
        if (!docsByCategory[doc.category._id]) {
          docsByCategory[doc.category._id] = [];
        }
        docsByCategory[doc.category._id].push(doc);
      } else {
        uncategorizedDocs.push(doc);
      }
    });

    // Add uncategorized documents if any
    if (uncategorizedDocs.length > 0) {
      docsByCategory['uncategorized'] = uncategorizedDocs;
    }

    setCategorizedDocuments(docsByCategory);
    setCategoryTree(categories);
  };

  const toggleCategory = (categoryId) => {
    const newExpanded = new Set(expandedCategories);
    if (newExpanded.has(categoryId)) {
      newExpanded.delete(categoryId);
    } else {
      newExpanded.add(categoryId);
    }
    setExpandedCategories(newExpanded);
  };

  const handleViewDocument = async (document) => {
    setViewerLoading(true);
    setSelectedDocument(document);
    setViewerLoading(false);
  };

  const closeViewer = () => {
    setSelectedDocument(null);
  };

  const getDocumentUrl = (filePath) => {
    return documentAPI.download(filePath);
  };

  const getFileExtension = (filePath) => {
    // Extract filename from path and get extension
    const filename = filePath.split(/[\\/]/).pop(); // Handle both forward and back slashes
    return filename.split('.').pop().toLowerCase();
  };

  const renderDocumentViewer = () => {
    if (!selectedDocument) return null;

    const fileExtension = getFileExtension(selectedDocument.filePath);
    const documentUrl = getDocumentUrl(selectedDocument.filePath);

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-lg shadow-xl w-full h-full max-w-6xl max-h-full flex flex-col">
          {/* Header */}
          <div className="flex items-center justify-between p-4 border-b">
            <div className="flex items-center space-x-3">
              <FileText className="h-6 w-6 text-blue-600" />
              <div>
                <h3 className="text-lg font-medium text-gray-900">
                  {selectedDocument.title}
                </h3>
                <p className="text-sm text-gray-500">
                  Uploaded on {new Date(selectedDocument.createdAt).toLocaleDateString()}
                </p>
              </div>
            </div>
            <button
              onClick={closeViewer}
              className="p-2 hover:bg-gray-100 rounded-full"
            >
              <X className="h-6 w-6 text-gray-500" />
            </button>
          </div>

          {/* Document Content */}
          <div className="flex-1 overflow-hidden">
            {viewerLoading ? (
              <div className="flex items-center justify-center h-full">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
              </div>
            ) : (
              <div className="h-full w-full">
                {['pdf'].includes(fileExtension) ? (
                  <iframe
                    src={documentUrl}
                    className="w-full h-full border-0"
                    title={selectedDocument.title}
                  />
                ) : ['jpg', 'jpeg', 'png', 'gif', 'bmp', 'webp'].includes(fileExtension) ? (
                  <div className="h-full flex items-center justify-center bg-gray-50">
                    <img
                      src={documentUrl}
                      alt={selectedDocument.title}
                      className="max-w-full max-h-full object-contain"
                    />
                  </div>
                ) : ['txt', 'md', 'csv'].includes(fileExtension) ? (
                  <iframe
                    src={documentUrl}
                    className="w-full h-full border-0"
                    title={selectedDocument.title}
                  />
                ) : ['doc', 'docx', 'xls', 'xlsx', 'ppt', 'pptx'].includes(fileExtension) ? (
                  <iframe
                    src={`https://docs.google.com/viewer?url=${encodeURIComponent(documentUrl)}&embedded=true`}
                    className="w-full h-full border-0"
                    title={selectedDocument.title}
                  />
                ) : (
                  <div className="h-full flex items-center justify-center bg-gray-50">
                    <div className="text-center">
                      <FileText className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                      <h3 className="text-lg font-medium text-gray-900 mb-2">
                        Preview not available
                      </h3>
                      <p className="text-gray-500">
                        This file type ({fileExtension}) cannot be previewed directly.
                      </p>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };

  const renderCategory = (category, level = 0) => {
    const isExpanded = expandedCategories.has(category._id);
    const hasDocuments = categorizedDocuments[category._id] && categorizedDocuments[category._id].length > 0;
    const hasSubcategories = category.subcategories && category.subcategories.length > 0;
    const hasContent = hasDocuments || hasSubcategories;

    return (
      <div key={category._id} className="border-l-2 border-gray-200">
        <div 
          className={`flex items-center py-2 px-4 hover:bg-gray-50 cursor-pointer ${
            level === 0 ? 'bg-gray-100' : level === 1 ? 'bg-gray-50' : 'bg-white'
          }`}
          style={{ paddingLeft: `${16 + level * 24}px` }}
          onClick={() => hasContent && toggleCategory(category._id)}
        >
          <div className="flex items-center space-x-2 flex-1">
            {hasContent ? (
              isExpanded ? (
                <ChevronDown className="h-4 w-4 text-gray-500" />
              ) : (
                <ChevronRight className="h-4 w-4 text-gray-500" />
              )
            ) : (
              <div className="w-4 h-4" />
            )}
            
            {isExpanded ? (
              <FolderOpen className="h-5 w-5 text-blue-500" />
            ) : (
              <Folder className="h-5 w-5 text-gray-500" />
            )}
            
            <span className={`font-medium ${
              level === 0 ? 'text-gray-900' : level === 1 ? 'text-gray-800' : 'text-gray-700'
            }`}>
              {category.name}
            </span>
          </div>
          
          {hasDocuments && (
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
              {categorizedDocuments[category._id].length}
            </span>
          )}
        </div>

        {isExpanded && hasContent && (
          <div className="ml-4">
            {/* Render documents for this category */}
            {hasDocuments && (
              <div className="space-y-1 mb-2">
                {categorizedDocuments[category._id].map((document) => (
                  <div 
                    key={document._id} 
                    className="flex items-center justify-between py-2 px-4 hover:bg-blue-50 rounded-md mx-2"
                    style={{ paddingLeft: `${16 + (level + 1) * 24}px` }}
                  >
                    <div className="flex items-center space-x-3 flex-1">
                      <FileText className="h-4 w-4 text-blue-600" />
                      <div>
                        <p className="text-sm font-medium text-blue-600 truncate">
                          {document.title}
                        </p>
                        <p className="text-xs text-gray-500">
                          {new Date(document.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                    <button
                      onClick={() => handleViewDocument(document)}
                      className="inline-flex items-center px-2 py-1 border border-transparent text-xs font-medium rounded text-blue-700 bg-blue-100 hover:bg-blue-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      <Eye className="h-3 w-3 mr-1" />
                      View
                    </button>
                  </div>
                ))}
              </div>
            )}

            {/* Render subcategories */}
            {hasSubcategories && (
              <div>
                {category.subcategories.map(subcategory => 
                  renderCategory(subcategory, level + 1)
                )}
              </div>
            )}
          </div>
        )}
      </div>
    );
  };

  const renderUncategorizedDocuments = () => {
    const uncategorizedDocs = categorizedDocuments['uncategorized'];
    if (!uncategorizedDocs || uncategorizedDocs.length === 0) return null;

    const isExpanded = expandedCategories.has('uncategorized');

    return (
      <div className="border-l-2 border-gray-200">
        <div 
          className="flex items-center py-2 px-4 hover:bg-gray-50 cursor-pointer bg-gray-100"
          onClick={() => toggleCategory('uncategorized')}
        >
          <div className="flex items-center space-x-2 flex-1">
            {isExpanded ? (
              <ChevronDown className="h-4 w-4 text-gray-500" />
            ) : (
              <ChevronRight className="h-4 w-4 text-gray-500" />
            )}
            
            {isExpanded ? (
              <FolderOpen className="h-5 w-5 text-gray-500" />
            ) : (
              <Folder className="h-5 w-5 text-gray-500" />
            )}
            
            <span className="font-medium text-gray-900">
              Uncategorized
            </span>
          </div>
          
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
            {uncategorizedDocs.length}
          </span>
        </div>

        {isExpanded && (
          <div className="ml-4 space-y-1 mb-2">
            {uncategorizedDocs.map((document) => (
              <div 
                key={document._id} 
                className="flex items-center justify-between py-2 px-4 hover:bg-blue-50 rounded-md mx-2"
                style={{ paddingLeft: '40px' }}
              >
                <div className="flex items-center space-x-3 flex-1">
                  <FileText className="h-4 w-4 text-blue-600" />
                  <div>
                    <p className="text-sm font-medium text-blue-600 truncate">
                      {document.title}
                    </p>
                    <p className="text-xs text-gray-500">
                      {new Date(document.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                </div>
                <button
                  onClick={() => handleViewDocument(document)}
                  className="inline-flex items-center px-2 py-1 border border-transparent text-xs font-medium rounded text-blue-700 bg-blue-100 hover:bg-blue-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  <Eye className="h-3 w-3 mr-1" />
                  View
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  const totalDocuments = documents.filter(doc =>
    doc.title.toLowerCase().includes(searchTerm.toLowerCase())
  ).length;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900">
            Document Library
          </h3>
          <p className="mt-1 text-sm text-gray-500">
            Browse and view documents organized by categories. Click on categories to expand and view documents.
          </p>
        </div>
      </div>

      {/* Search */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <div>
            <label htmlFor="search" className="block text-sm font-medium text-gray-700">
              Search Documents
            </label>
            <div className="mt-1 relative rounded-md shadow-sm">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Search className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                id="search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="focus:ring-blue-500 focus:border-blue-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md"
                placeholder="Search by document title..."
              />
            </div>
          </div>
        </div>
      </div>

      {/* Results Summary */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-3 sm:px-6 flex items-center justify-between">
          <p className="text-sm text-gray-700">
            {searchTerm ? `Found ${totalDocuments} documents matching "${searchTerm}"` : `${totalDocuments} documents available`}
          </p>
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setExpandedCategories(new Set())}
              className="text-sm text-gray-500 hover:text-gray-700"
            >
              Collapse All
            </button>
            <button
              onClick={() => {
                const allIds = new Set();
                const addIds = (cats) => {
                  cats.forEach(cat => {
                    if (categorizedDocuments[cat._id] || cat.subcategories?.length > 0) {
                      allIds.add(cat._id);
                    }
                    if (cat.subcategories) {
                      addIds(cat.subcategories);
                    }
                  });
                };
                addIds(categoryTree);
                if (categorizedDocuments['uncategorized']) {
                  allIds.add('uncategorized');
                }
                setExpandedCategories(allIds);
              }}
              className="text-sm text-blue-600 hover:text-blue-800"
            >
              Expand All
            </button>
          </div>
        </div>
      </div>

      {/* Category Tree */}
      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        {categoryTree.length > 0 || categorizedDocuments['uncategorized'] ? (
          <div className="divide-y divide-gray-200">
            {categoryTree.map(category => renderCategory(category))}
            {renderUncategorizedDocuments()}
          </div>
        ) : (
          <div className="text-center py-12">
            <FileText className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No documents found</h3>
            <p className="mt-1 text-sm text-gray-500">
              {searchTerm
                ? 'Try adjusting your search criteria.'
                : 'No documents have been uploaded yet.'}
            </p>
          </div>
        )}
      </div>

      {/* Document Viewer Modal */}
      {renderDocumentViewer()}
    </div>
  );
};

export default Documents;
